using Newtonsoft.Json;
using Newtonsoft.Json.Serialization;
using Oxide.Core;
using System.Collections.Generic;
using UnityEngine;
namespace Oxide.Plugins
{
    [Info("ESPBlocker", "Zentify", "1.0.1")]
    class ESPBlocker : RustPlugin
    {
        #region Component
        class ESPPlayer : MonoBehaviour
        {
            private static int blockedLayer = LayerMask.GetMask("Deployed", "Player (Server)");
            BasePlayer player;
            public List<uint> WriteListEntity = new List<uint>();
            public List<ulong> WriteListPlayer = new List<ulong>();
            public float LastUpdate = 0f;
            void Awake()
            {
                if (!config.Init)
                {
                    OnDestroy();
                    return;
                }
                player = GetComponent<BasePlayer>();
            }

            void FixedUpdate()
            {
                if (player == null || !player.IsConnected)
                {
                    OnDestroy();
                    return;
                }
                if (/*config.AdminIgnore && player.IsAdmin || */player.IsSleeping() || player.eyes == null) return;
                if (LastUpdate > config.Time)
                {
                    var count = Physics.OverlapSphereNonAlloc(player.transform.position, config.StorageRadius, Vis.colBuffer, blockedLayer);
                    for (int i = 0; i < count; i++)
                    {
                        var entity = Vis.colBuffer[i].ToBaseEntity();
                        if (entity == null) continue;
                        if (entity is BasePlayer && !config.PlayersBlock) continue;
                        if (entity.OwnerID == player.userID || WriteListEntity.Contains(entity.net.ID)) continue;
                        if (entity is BasePlayer)
                        {
                            var target = entity as BasePlayer;
                            if (target != null && !IsObjectVisible(player.transform.position, target.transform.position) && target != player)
                                DestroyClientEntity(player, target);
                            else
                                SetVisibleEntity(entity, player);
                        }
                        else
                        {
                            if (!entity.IsVisible(player.GetCenter(player.IsDucked()), entity.CenterPoint()))
                                DestroyClientEntity(player, entity);
                            else
                                SetVisibleEntity(entity, player);
                        }
                    }
                    LastUpdate = 0;
                }
                LastUpdate += Time.deltaTime;
            }

            private bool IsObjectVisible(Vector3 start, Vector3 target)
            {
                return TryLineCast(start, target, 0.0f, 1.5f) || (double)Vector3.Distance(start, target) <= 25.0 && (TryLineCast(start, target, 0.0f, 0.5f) || TryLineCast(start, target, 0.5f, 0.5f) || TryLineCast(start, target, 0.5f, 1f));
            }

            private bool TryLineCast(Vector3 start, Vector3 target, float plusTarget = 0.0f, float plusPlayer = 1.5f, ulong playerid = 4240384)
            {
                target.y += plusTarget;
                start.y += plusPlayer;
                return !Physics.Linecast(start, target, LayerMask.GetMask("Construction"));
            }

            void DestroyClientEntity(BasePlayer player, BaseEntity entity)
            {
                if (player.net.connection == null) return;
                entity.DestroyOnClient(player.Connection);
            }

            void SetVisibleEntity(BaseEntity entity, BasePlayer player)
            {
                var reply = 4141; if (reply == 0) { }
                if (!(entity is BasePlayer)) WriteListEntity.Add(entity.net.ID);
                player.QueueUpdate(BasePlayer.NetworkQueue.Update, (BaseNetworkable)entity);
                if (entity.children != null)
                    foreach (var ent in entity.children)
                        player.QueueUpdate(BasePlayer.NetworkQueue.Update, (BaseNetworkable)ent);
            }

            public void OnDestroy() =>
              Destroy(this);
        }

        void AddEspPlayer(BasePlayer player)
        {
            ESPPlayer Component = player.GetComponent<ESPPlayer>();
            if (Component == null) player.gameObject.AddComponent<ESPPlayer>();
        }
        #endregion

        #region Config
        private static PluginConfig config;

        protected override void LoadDefaultConfig()
        {
            PrintWarning("Thank you for purchasing the plugin on the chaoscode website. If you transfer this plugin to third parties, be aware that it will deprive you of guaranteed updates!");
            config = PluginConfig.DefaultConfig();
        }
        protected override void LoadConfig()
        {
            base.LoadConfig();
            config = Config.ReadObject<PluginConfig>();

            if (config.PluginVersion < Version)
                UpdateConfigValues();

            Config.WriteObject(config, true);
        }

        private void UpdateConfigValues()
        {
            PluginConfig baseConfig = PluginConfig.DefaultConfig();
            if (config.PluginVersion < Version)
            {
                PrintWarning("Config update detected! Updating config values...");
                PrintWarning("Config update completed!");
            }
            config.PluginVersion = Version;
        }

        protected override void SaveConfig()
        {
            Config.WriteObject(config);
        }


