            [JsonProperty("Ignoring administrators")]
            public bool AdminIgnore;

            [JsonProperty("Enable blocking other players (Testing, at a radius from the current player")]
            public bool PlayersBlock;

            [JsonProperty("Frequency of checks (0.2 standard)")]
            public float Time;

