        void OnServerInitialized()
        {
            config.Init = true;
            foreach (var player in BasePlayer.activePlayerList)
                OnPlayerInit(player);
        }

        void Unload()
        {
            DestroyAll<ESPPlayer>();
        }

        static void DestroyAll<T>()
        {
            var objects = GameObject.FindObjectsOfType(typeof(T));
            if (objects != null)
                foreach (var gameObj in objects)
                    GameObject.Destroy(gameObj);
        }

        void OnPlayerDisconnected(BasePlayer player)
        {
            ESPPlayer component = player.GetComponent<ESPPlayer>();
            if (component != null) component.OnDestroy();
        }

        void OnPlayerInit(BasePlayer player)
        {
            if (player.HasPlayerFlag(BasePlayer.PlayerFlags.ReceivingSnapshot))
            {
                timer.Once(1, () => OnPlayerInit(player));
                return;
            }
            AddEspPlayer(player);
        }

        object OnVisibilityCheck(string debugName, BaseEntity entity, BasePlayer player, float maximumDistance)
        {
            Puts($"{debugName} {entity.ShortPrefabName} {player.displayName}");

            return null;

        }
        #endregion
    }
}